import React from 'react';
import './Resources.css';

const Resources = () => {
  const resourceCategories = [
    {
      title: 'Research Guides',
      description: 'Step-by-step guides covering research methodologies, writing, and best practices.',
      icon: 'üìñ',
      count: '25+ Guides'
    },
    {
      title: 'Templates & Tools',
      description: 'Download research proposal templates, data collection forms, and analysis tools.',
      icon: 'üõ†Ô∏è',
      count: '40+ Templates'
    },
    {
      title: 'Datasets',
      description: 'Access open datasets for your research projects across various disciplines.',
      icon: 'üìä',
      count: '100+ Datasets'
    },
    {
      title: 'Video Tutorials',
      description: 'Learn through comprehensive video courses on research skills and techniques.',
      icon: 'üé•',
      count: '50+ Videos'
    },
    {
      title: 'Webinars',
      description: 'Join live sessions with experts on trending topics in research and innovation.',
      icon: 'üíª',
      count: 'Monthly Events'
    },
    {
      title: 'Publications',
      description: 'Read our quarterly journal featuring outstanding youth research from Tanzania.',
      icon: 'üìö',
      count: '12 Issues'
    }
  ];

  return (
    <section className="resources section">
      <div className="container">
        <h2 className="section-title">Resources Hub</h2>
        <p className="section-subtitle">
          Access comprehensive learning materials, tools, and datasets to support your research journey.
        </p>

        <div className="resources-grid">
          {resourceCategories.map((category, index) => (
            <div key={index} className="resource-card card">
              <div className="resource-header">
                <div className="resource-icon">{category.icon}</div>
                <span className="resource-count">{category.count}</span>
              </div>
              <h3>{category.title}</h3>
              <p>{category.description}</p>
              <button className="btn btn-outline">Explore</button>
            </div>
          ))}
        </div>

        <div className="resources-featured">
          <div className="featured-content card">
            <h3>üì¢ Featured Resource</h3>
            <h2>Complete Research Methodology Guide 2026</h2>
            <p>A comprehensive 150-page guide covering everything from research design to publication. Perfect for beginners and experienced researchers.</p>
            <button className="btn btn-primary">Download Free Guide</button>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Resources;
