import React from 'react';
import Icon from '../Common/Icon';
import './Features.css';

const Features = () => {
  const features = [
    {
      title: 'Research Portal',
      description: 'Submit, review, and publish your research. Access a growing repository of youth-led innovations.',
      icon: 'book'
    },
    {
      title: 'Events & Competitions',
      description: 'Participate in national research competitions and innovation challenges with exciting opportunities.',
      icon: 'trophy'
    },
    {
      title: 'Mentorship Network',
      description: 'Connect with experienced mentors and industry professionals to guide your research journey.',
      icon: 'users'
    },
    {
      title: 'Learning Resources',
      description: 'Access research guides, templates, datasets, and webinars to enhance your research skills.',
      icon: 'lightbulb'
    }
  ];

  return (
    <section className="features section">
      <div className="container">
        <h2 className="section-title">Platform Features</h2>
        <p className="section-subtitle">
          Everything you need to start your research and innovation journey
        </p>

        <div className="features-grid">
          {features.map((feature, index) => (
            <div key={index} className="feature-card card">
              <div className="feature-icon"><Icon name={feature.icon} size={48} color="#df8d31" /></div>
              <h3>{feature.title}</h3>
              <p>{feature.description}</p>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Features;
