import React from 'react';
import Icon from '../Common/Icon';
import './Events.css';

const Events = () => {
  const upcomingEvents = [
    {
      title: 'National Research Competition 2026',
      date: 'March 15-17, 2026',
      location: 'Dar es Salaam',
      category: 'Competition',
      description: 'Showcase your research and compete for prizes and recognition.'
    },
    {
      title: 'Innovation Summit',
      date: 'April 22, 2026',
      location: 'Arusha',
      category: 'Conference',
      description: 'Network with researchers, innovators, and industry leaders.'
    },
    {
      title: 'Youth Research Workshop',
      date: 'May 5-6, 2026',
      location: 'Mwanza',
      category: 'Workshop',
      description: 'Learn research methodologies and best practices from experts.'
    }
  ];

  return (
    <section className="events section">
      <div className="container">
        <h2 className="section-title">Events & Competitions</h2>
        <p className="section-subtitle">
          Participate in research competitions, workshops, and networking events across Tanzania.
        </p>

        <div className="events-grid">
          {upcomingEvents.map((event, index) => (
            <div key={index} className="event-card card">
              <div className="event-category">{event.category}</div>
              <h3>{event.title}</h3>
              <div className="event-details">
                <div className="event-detail">
                  <span className="event-icon"><Icon name="calendar" size={20} color="#093344" /></span>
                  <span>{event.date}</span>
                </div>
                <div className="event-detail">
                  <span className="event-icon"><Icon name="mapPin" size={20} color="#093344" /></span>
                  <span>{event.location}</span>
                </div>
              </div>
              <p>{event.description}</p>
              <button className="btn btn-primary">Register Now</button>
            </div>
          ))}
        </div>

        <div className="events-cta">
          <button className="btn btn-outline">View All Events</button>
        </div>
      </div>
    </section>
  );
};

export default Events;
