import React from 'react';
import Icon from '../Common/Icon';
import './Mentorship.css';

const Mentorship = () => {
  const mentorshipBenefits = [
    {
      title: 'Expert Guidance',
      description: 'Get one-on-one mentorship from experienced researchers and industry professionals.',
      icon: 'graduationCap'
    },
    {
      title: 'Career Development',
      description: 'Receive guidance on academic paths, career opportunities, and skill development.',
      icon: 'briefcase'
    },
    {
      title: 'Network Building',
      description: 'Connect with like-minded peers and build lasting professional relationships.',
      icon: 'handshake'
    },
    {
      title: 'Research Support',
      description: 'Get help with research design, methodology, analysis, and publication.',
      icon: 'microscope'
    }
  ];

  const mentorTypes = [
    'Academic Researchers',
    'Industry Professionals',
    'Successful Entrepreneurs',
    'Published Authors',
    'PhD Candidates',
    'Innovation Experts'
  ];

  return (
    <section className="mentorship section">
      <div className="container">
        <h2 className="section-title">Mentorship Network</h2>
        <p className="section-subtitle">
          Connect with experienced mentors who are passionate about guiding the next generation of researchers.
        </p>

        <div className="mentorship-benefits">
          {mentorshipBenefits.map((benefit, index) => (
            <div key={index} className="mentorship-card card">
              <div className="mentorship-icon"><Icon name={benefit.icon} size={48} color="#df8d31" /></div>
              <h3>{benefit.title}</h3>
              <p>{benefit.description}</p>
            </div>
          ))}
        </div>

        <div className="mentor-types">
          <h3>Find Your Mentor</h3>
          <div className="mentor-tags">
            {mentorTypes.map((type, index) => (
              <span key={index} className="mentor-tag">{type}</span>
            ))}
          </div>
        </div>

        <div className="mentorship-cta">
          <button className="btn btn-primary">Find a Mentor</button>
          <button className="btn btn-outline">Become a Mentor</button>
        </div>
      </div>
    </section>
  );
};

export default Mentorship;
