import React from 'react';
import './MeetExperts.css';

const MeetExperts = () => {
  return (
    <section className="meet-experts">
      <div className="container">
        <div className="experts-header">
          <div className="experts-icon">
            <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
              <circle cx="12" cy="12" r="10"/>
              <path d="M12 2 L12 22 M2 12 L22 12 M6 6 L18 18 M18 6 L6 18"/>
            </svg>
          </div>
          <h2>Meet the experts</h2>
        </div>

        <div className="experts-image">
          <img src="/images/experts-team.jpg" alt="YRIF Experts and Mentors" />
        </div>

        <div className="experts-cta">
          <p>Connect with experienced researchers, mentors, and industry professionals 
             who are ready to guide you on your innovation journey.</p>
          <a href="/mentorship" className="btn btn-primary">Find a Mentor</a>
        </div>
      </div>
    </section>
  );
};

export default MeetExperts;
