import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import './Hero.css';

const Hero = () => {
  // Image arrays for rotation (add more images to /public/images to expand)
  const biologyImages = [
    '/images/biology.jpg',
    '/images/biology-2.jpg',
    '/images/biology-3.jpg'
  ];
  const harmonyImages = [
    '/images/meditation.jpg',
    '/images/harmony.jpg',
    '/images/harmony-2.jpg'
  ];
  const geneticsImages = [
    '/images/engineering.jpg',
    '/images/genetics.jpg',
    '/images/genetics-2.jpg'
  ];

  const [bioIndex, setBioIndex] = useState(0);
  const [harmIndex, setHarmIndex] = useState(0);
  const [genIndex, setGenIndex] = useState(0);

  useEffect(() => {
    const timers = [
      setInterval(() => setBioIndex(i => (i + 1) % biologyImages.length), 6000),
      setInterval(() => setHarmIndex(i => (i + 1) % harmonyImages.length), 7000),
      setInterval(() => setGenIndex(i => (i + 1) % geneticsImages.length), 8000)
    ];
    return () => timers.forEach(clearInterval);
  }, []);

  const handleImageError = (e, fallback) => {
    e.currentTarget.src = fallback;
  };

  return (
    <section id="home" className="hero">
      <div className="container">
        <div className="hero-content">
          <div className="hero-text">
            <h1 className="hero-title">
              Empowering Tanzania's Youth Researchers
            </h1>

            <p className="hero-description">
              Join the Youth Research & Innovation Foundation (YRIF) - Tanzania's premier platform connecting young researchers,
              innovators, and mentors. Access resources, submit your research, participate in competitions, and shape the future
              of innovation in Tanzania.
            </p>

            <div className="hero-cta">
              <Link to="/login" className="btn btn-primary btn-large">
                Get Started
              </Link>
              <a href="#research" className="btn btn-outline btn-large">
                Explore Research
              </a>
            </div>
          </div>

          <div className="hero-visual">
            <div className="hero-cards">
              <div className="hero-card biology">
                <img
                  src={biologyImages[bioIndex]}
                  alt="Biology Research"
                  onError={(e) => handleImageError(e, biologyImages[0])}
                />
                <div className="hero-card-label">Biology</div>
              </div>

              <div className="hero-card harmony">
                <img
                  src={harmonyImages[harmIndex]}
                  alt="Social Research"
                  onError={(e) => handleImageError(e, harmonyImages[0])}
                />
                <div className="hero-card-label">Harmony</div>
              </div>

              <div className="hero-card genetics">
                <img
                  src={geneticsImages[genIndex]}
                  alt="Technology & Innovation"
                  onError={(e) => handleImageError(e, geneticsImages[0])}
                />
                <div className="hero-card-label">Genetics</div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Hero;
