import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import './Hero.css';

const Hero = () => {
  const biologyLabels = ['Biology', 'Ecology', 'Microbiology'];
  const harmonyLabels = ['Harmony', 'Sociology', 'Community'];
  const geneticsLabels = ['Genetics', 'Bioinformatics', 'CRISPR'];

  const [biologyIndex, setBiologyIndex] = useState(0);
  const [harmonyIndex, setHarmonyIndex] = useState(0);
  const [geneticsIndex, setGeneticsIndex] = useState(0);

  useEffect(() => {
    const bioTimer = setInterval(() => {
      setBiologyIndex((i) => (i + 1) % biologyLabels.length);
    }, 5000);

    const harTimer = setInterval(() => {
      setHarmonyIndex((i) => (i + 1) % harmonyLabels.length);
    }, 6500);

    const genTimer = setInterval(() => {
      setGeneticsIndex((i) => (i + 1) % geneticsLabels.length);
    }, 8000);

    return () => {
      clearInterval(bioTimer);
      clearInterval(harTimer);
      clearInterval(genTimer);
    };
  }, []);
  return (
    <section id="home" className="hero">
      <div className="container">
        <div className="hero-content">
          <div className="hero-text">
            <h1 className="hero-title">
              Empowering Tanzania's Youth Researchers
            </h1>

            <p className="hero-description">
              Tanzania's premier platform connecting young researchers,
              innovators, and mentors. Access resources, submit your research, participate in competitions, and shape the future
              of innovation in Tanzania.
            </p>

            <div className="hero-cta">
              <Link to="/login" className="btn btn-primary btn-large">
                Get Started
              </Link>
              <a href="#research" className="btn btn-outline btn-large">
                Explore Research
              </a>
            </div>
          </div>

          <div className="hero-visual">
            <div className="hero-cards">
              <div className="hero-card biology">
                <img src="/images/biology.jpg" alt="Biology Research" />
                <div className="hero-card-label">{biologyLabels[biologyIndex]}</div>
              </div>

              <div className="hero-card harmony">
                <img src="/images/meditation.jpg" alt="Social Research" />
                <div className="hero-card-label">{harmonyLabels[harmonyIndex]}</div>
              </div>

              <div className="hero-card genetics">
                <img src="/images/engineering.jpg" alt="Technology & Innovation" />
                <div className="hero-card-label">{geneticsLabels[geneticsIndex]}</div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Hero;
