import React from 'react';
import './Research.css';

const Research = () => {
  const researchFeatures = [
    {
      title: 'Submit Research',
      description: 'Share your innovative research projects and get feedback from peers and experts.',
      icon: 'üìù'
    },
    {
      title: 'Browse Repository',
      description: 'Explore a vast collection of youth-led research across various disciplines.',
      icon: 'üîç'
    },
    {
      title: 'Peer Review',
      description: 'Participate in the peer review process and help validate quality research.',
      icon: '‚úÖ'
    },
    {
      title: 'Get Published',
      description: 'High-quality research gets featured in our quarterly journal and digital library.',
      icon: 'üì∞'
    }
  ];

  return (
    <section className="research section">
      <div className="container">
        <h2 className="section-title">Research Portal</h2>
        <p className="section-subtitle">
          Submit, review, and publish your research. Join Tanzania's growing community of young researchers.
        </p>

        <div className="research-grid">
          {researchFeatures.map((feature, index) => (
            <div key={index} className="research-card card">
              <div className="research-icon">{feature.icon}</div>
              <h3>{feature.title}</h3>
              <p>{feature.description}</p>
            </div>
          ))}
        </div>

        <div className="research-cta">
          <button className="btn btn-primary">Submit Your Research</button>
          <button className="btn btn-outline">Browse Repository</button>
        </div>
      </div>
    </section>
  );
};

export default Research;
