import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import './Hero.css';

const Hero = () => {
  const [activeCard, setActiveCard] = useState(0);

  useEffect(() => {
    const id = setInterval(() => {
      setActiveCard((prev) => (prev + 1) % 3);
    }, 4000);
    return () => clearInterval(id);
  }, []);
  return (
    <section id="home" className="hero">
      <div className="container">
        <div className="hero-content">
          <div className="hero-text">
            <h1 className="hero-title">
              Empowering Tanzania's Youth Researchers
            </h1>

            <p className="hero-description">
              Join the Youth Research & Innovation Foundation (YRIF) - Tanzania's premier platform connecting young researchers,
              innovators, and mentors. Access resources, submit your research, participate in competitions, and shape the future
              of innovation in Tanzania.
            </p>

            <div className="hero-cta">
              <Link to="/login" className="btn btn-primary btn-large">
                Get Started
              </Link>
              <a href="#research" className="btn btn-outline btn-large">
                Explore Research
              </a>
            </div>
          </div>

          <div className="hero-visual">
            <div className="hero-cards rotating">
              <div className={`hero-card biology ${activeCard === 0 ? 'active' : ''}`}>
                <img src="/images/biology.jpg" alt="Biology Research" />
                <div className="hero-card-label">Biology</div>
              </div>

              <div className={`hero-card harmony ${activeCard === 1 ? 'active' : ''}`}>
                <img src="/images/meditation.jpg" alt="Social Research" />
                <div className="hero-card-label">Harmony</div>
              </div>

              <div className={`hero-card genetics ${activeCard === 2 ? 'active' : ''}`}>
                <img src="/images/engineering.jpg" alt="Technology & Innovation" />
                <div className="hero-card-label">Genetics</div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Hero;
